changeSet.sql:

DROP TABLE IF EXISTS user;
CREATE TABLE user ( user_id int primary key auto_increment not null,
                    email varchar(100) not null,
                    password varchar(100) not null,
                    create_at timestamp,
                    expired boolean) ENGINE=InnoDB;

DROP TABLE IF EXISTS role;
CREATE TABLE role ( role_id int primary key auto_increment not null,
                    name varchar(20) not null,
                    description varchar(300)) ENGINE=InnoDB;

DROP TABLE IF EXISTS user_roles;
CREATE TABLE user_roles ( user_id int not null,
                          role_id int not null,
                          constraint unit_user_role unique ( user_id, role_id)) ENGINE=InnoDB;

ALTER TABLE user_roles ADD FOREIGN KEY (user_id) REFERENCES user(user_id) on update cascade on delete restrict;

ALTER TABLE user_roles ADD FOREIGN KEY (role_id) REFERENCES role(role_id) on update cascade on delete restrict;
DROP TABLE IF EXISTS word;
CREATE TABLE word ( word_id int primary key auto_increment not null,
                    english varchar(100) not null unique,
                    spelling varchar(200) not null,
                    type_of_word varchar(200),
                    meaning text,
                    example text,
                    extra_information varchar(5000)) ENGINE=InnoDB;
